mapper.update:
update customers
set cusName=#{cusName}, classification=#{classification}, phoneNo=#{phoneNo}, address=#{address}
where cusName=#{cusName}

mapper.select:
select cusName, phoneNo, address
from customers
where cusName=#{cusName}
